<script type="text/javascript">

// note that these should all end up in session cookies to emulate the behaviour of the java applet
var siteId;
var siteName;
var dailyCode;
var password;

function getDailyCodeCB(data)
{
    dailyCode = $(data).find('DailyCode').text();
      
    // now get the site info
    db_execute("SITEINFO", getSiteInfoCB);
}

function getSiteInfoCB(data)
{
    siteId = $(data).find('SiteID').text();
    siteName = $(data).find('Description').text();

    document.getElementById("siteMsg").childNodes[0].data = "Welcome to " + siteName;
    document.getElementById("codeMsg").childNodes[0].data = "Today's daily code is " + dailyCode;

    // SoftwareVersion is provided by including the version.js file
    var ver = SoftwareVersion;
    if( ver != null ) 
    {
        ver = ver.substring( 6 ); // 206V0RX.XX -> X.XX
        ver = ver.replace( ".", "" ); // X.XX -> XXX
    }
    else 
    {
        ver = "???";
    }

    // now set the interesting data in session cookies
    $.cookie('SiteName', siteName);
    $.cookie('SiteID', siteId);
    $.cookie('ProductType', ProductType);

    var extra = "Note - SMS is NOT sensitive to either case or whitespace in the site name and there are spaces separating the first two numbers.";
    // For phase 2, strip whitespace and turn into lower case
    siteName = siteName.replace( / /g, "" );
    siteName = siteName.toLowerCase();

    document.getElementById("SMSInfo").childNodes[0].data = "SMS Details: '" + ver + " " + dailyCode + " " + siteName + "'";
    document.getElementById("SMSExtra").childNodes[0].data = extra;      
}

function loaded() 
{
    // clear existing cookies if present
    $.cookie('User', null);
    $.cookie('Hash', null);
    $.cookie('Password', null);
    $.cookie('AccessLevel', null);
    $.cookie('SessionCode', null);
    $.cookie('SelectedTopTab', null);
	$.cookie('ProductType', null);

	if (ProductType == 2)
	{
		var cssLink = $("<link rel='stylesheet' type='text/css' href='el.css'>");
		$("head").append(cssLink);
		document.getElementById("productTitle").innerHTML = "ConnectCall";
	}
	else
	{
		document.getElementById("productTitle").innerHTML = "Communicall Vi";
	}
	
    // next, get the daily code.  This will also retrieve the site info
    db_execute("DAILYCODE", getDailyCodeCB);
}

function submitLogin() 
{
    var username = document.getElementById("user").value;
    password = document.getElementById("pass").value;
    var shortPass = (username == "Service") ? password.substring( 0, password.length - 1 ) : password; // Strip off final character

    // login stuff
    var accessLevel = db_doLogin(username, shortPass);
    if (accessLevel >= 0) 
    { 
        window.location = "MainIndex.html";
    } 
    else 
    {
        alert("Login invalid, please check your username and password and try again.");
        document.getElementById("user").value="";
        document.getElementById("pass").value="";
    }
}

function clearText(caller) 
{
    document.getElementById(caller).value="";
}

/**
 *  Do the same as pressing the login button if the user has hit enter in the password field
 *  We return true from this if the keystroke should be allowed onwards, or false if we're doing the submit
 */
function enterLogin(caller, e)
{
    var keycode;
    if (window.event)
    {
        keycode = window.event.keyCode;
    }
    else if (e)
    {
        keycode = e.which;
    }
    else
    {
        return true;
    }
    
    if (keycode == 13)
    {
        submitLogin();
        return false;
    }
    else
    {
        return true;
    }
}

// set focus to the user name field by default
$(document).ready(function() 
{
    document.getElementById("user").focus();
});

</script>
