function residentCountCB(data)
{
    var count  = $(data).find('numRows').text();
    var hrow  = createrow(infoTable, null );
    createcell(infoTable, hrow, "Residents in Scheme: " + count);

    db_sysSummary(systemSummaryCB);
}

function systemSummaryCB(data)
{
    // populate a drop-down with valid site modes and the current one selected
    var currentSiteMode = $(data).find('systemstatus').text();

    // list valid site modes
    validSiteModeData = db_call_group_info_site_modes_get();
    
    hrow = createrow(infoTable, null);
    createcell(infoTable, hrow, "Scheme site mode: ", "");
    createdropdowncell( infoTable, hrow, "gridtxt", "", 0, "", "sitemodedropdown");    
    var buttonCell = document.createElement("td"); 
    hrow.appendChild(buttonCell); 	   

    // create Change button
    var changeButton = document.createElement("INPUT");
    changeButton.setAttribute("type", "button");
    changeButton.setAttribute("value", "Change");
    changeButton.onclick = function() {changeSiteMode();};
    buttonCell.appendChild(changeButton);

    // populate the site mode combo box
    var dropdown = document.getElementById("sitemodedropdown");
    var i = 0;
    
    $(validSiteModeData).each(function ()
    {
        var cgName = $(this).find("name").text();
        var cgID = parseInt($(this).find("cgId").text(), 10);
        dropdown.options[i] = new Option(cgName, cgID);
        if (cgID == currentSiteMode)
        {
            dropdown.selectedIndex = i;
        }
        i++;
    });

    db_executeQuery("Select * from TBL_SCHEME_MANAGERS WHERE Telephone_Line > 0 ORDER BY ID ASC", schemeManagersCB);
}

/**
 *  Attempt to change the site mode to the value selected
 */
function changeSiteMode()
{
    var dropdown = document.getElementById("sitemodedropdown");
    var selectedCG = dropdown.options[dropdown.selectedIndex].value;
    var result = db_site_mode_set(selectedCG);
    var statusCode = parseInt($(result).find("ChangeStatus").text(), 10);
    
    switch (statusCode)
    {
        case 0: alert("Site mode change successful"); break;
        case 1: alert("Site mode change failed"); break;
        case 2: alert("Site mode change failed: line 1 fault"); break;
        case 3: alert("Site mode change failed: line 2 fault"); break;
        default: alert("Site mode change failed"); break;
    }
}

